#include <gtest/gtest.h>
#include "tls.h"

static size_t from_hex(const char *hex, char *data) {
    char *pos = (char *)hex;
    size_t length = strlen(hex) / 2;
    size_t count;
    for (count = 0; count < length; count++) {
        sscanf(pos, "%2hhx", &data[count]);
        pos += 2;
    }
    return count;
}

